<?phpnamespace ingsw10;include "Encryptor.php";class Employee{    protected $username;    protected $hashedPassword;    /**     * Employee constructor.     * @param $password     * @param $username     */    public function __construct($username, $password)    {        $this->setHashedPassword($password, Encryptor::CPU_COST_LOW);        $this->username = $username;    }    /**     * @return mixed     */    public function verifyPassword($testPassword)    {        return Encryptor::verify($testPassword,$this->hashedPassword);    }    /**     * @param mixed $hashedPassword     */    public function setHashedPassword($hashedPassword, $cost)    {        $this->hashedPassword = Encryptor::hash($hashedPassword, $cost);    }    /**     * @return mixed     */    public function getUsername()    {        return $this->username;    }    /**     * @param mixed $username     */    public function setUsername($username)    {        $this->username = $username;    }}?>