<?php/*** Created by PhpStorm.* User: ozeta* Date: 18/03/2017* Time: 22:54*/namespace ingsw10;class Meter{private $id;private $customerID;private $address;private $addressNumber;private $city;private $cityProv;private $phone;/*** Meter constructor.* @param $address* @param $addressNumber* @param $city* @param $cityProv* @param $customerID* @param $id* @param $phone*/public function __construct($address, $addressNumber, $city, $cityProv, $customerID, $id, $phone){$this->address = $address;$this->addressNumber = $addressNumber;$this->city = $city;$this->cityProv = $cityProv;$this->customerID = $customerID;$this->id = $id;$this->phone = $phone;}/*** @return mixed*/public function getAddress(){return $this->address;}/*** @param mixed $address*/public function setAddress($address){$this->address = $address;}/*** @return mixed*/public function getAddressNumber(){return $this->addressNumber;}/*** @param mixed $addressNumber*/public function setAddressNumber($addressNumber){$this->addressNumber = $addressNumber;}/*** @return mixed*/public function getCity(){return $this->city;}/*** @param mixed $city*/public function setCity($city){$this->city = $city;}/*** @return mixed*/public function getCityProv(){return $this->cityProv;}/*** @param mixed $cityProv*/public function setCityProv($cityProv){$this->cityProv = $cityProv;}/*** @return mixed*/public function getCustomerID(){return $this->customerID;}/*** @param mixed $customerID*/public function setCustomerID($customerID){$this->customerID = $customerID;}/*** @return mixed*/public function getId(){return $this->id;}/*** @param mixed $id*/public function setId($id){$this->id = $id;}/*** @return mixed*/public function getPhone(){return $this->phone;}/*** @param mixed $phone*/public function setPhone($phone){$this->phone = $phone;}}?>